mapper.select:
SELECT t.tag_name
FROM diaries d
JOIN diary_tags dt ON d.diary_id = dt.diary_id
JOIN tags t ON dt.tag_id = t.tag_id
WHERE d.user_id = #{userId}
AND d.create_time BETWEEN #{startDate} AND #{endDate}

SELECT * FROM diaries
WHERE user_id = #{userId}
AND create_time BETWEEN #{startDate} AND #{endDate}

SELECT dt.diary_id as diary_id, t.tag_name as tag_name FROM diary_tags dt JOIN tags t ON dt.tag_id = t.tag_id WHERE dt.diary_id IN
#{diaryId}
ORDER BY dt.diary_id
